const getGroupId = ids => {
  const xor = Array.from(Array(9).keys()).slice(2) // 그룹은 최소 2
    .find(i =>
      ids.map(id => Math.floor(id / Math.pow(10, i)))
        .every((id, _, [first]) => first === id)
    );
  return Math.floor(ids[0] / Math.pow(10, xor)).toString();
};

